<template>
  <view
    v-if="show"
    class="au-loader"
    :class="mode == 'circle' ? 'au-loader-circle' : 'au-loader-flower'"
    :style="[cricleStyle]"
  >
  </view>
</template>

<script>
/**
 * loading 加载动画
 * @description 此组件为一个小动画，目前用在组件的正在加载状态场景。
 * @property {Boolean} show 是否显示动画（默认true）
 * @property {String} mode 模式选择，见官网说明（默认circle）
 * @property {String} color 动画活动区域的颜色，只对 mode = circle 模式有效（默认#8f8d8e)
 * @property {String Number} size 加载图标的大小，单位rpx（默认34）
 * @property {String} width 宽度 mode = circle 模式有效（默认2px)
 * @example <au-loader mode="circle"></au-loader>
 */
export default {
  name: 'au-loader',
  props: {
    // 是否显示动画
    show: {
      type: Boolean,
      default: true
    },
    // 动画的类型
    mode: {
      type: String,
      default: 'circle'
    },
    // 动画的颜色
    color: {
      type: String,
      default: '#8f8d8e'
    },
    // 加载图标的大小，单位rpx
    size: {
      type: [String, Number],
      default: '34'
    },
    // 宽度，只对mode=circle有效
    width: {
      type: String,
      default: '2px'
    }
  },
  computed: {
    // 加载中圆圈动画的样式
    cricleStyle() {
      let style = {}
      style.width = this.size + 'rpx'
      style.height = this.size + 'rpx'
      if (this.mode == 'circle') {
        style.borderLeftColor = this.color
        style.borderRightColor = this.color
        style.borderBottomColor = this.color
        style.borderWidth = this.width
      }
      return style
    }
  }
}
</script>

<style lang="scss" scoped>
@import '../../css/mixin.scss';

.au-loader-circle {
  /* #ifndef APP-NVUE */
  display: inline-flex;
  /* #endif */
  vertical-align: middle;
  width: 28rpx;
  height: 28rpx;
  background: 0 0;
  border-radius: 50%;
  border: 3px solid;
  border-color: #ffffff #8f8d8e #8f8d8e #8f8d8e;
  animation: au-circle 1s linear infinite;
}

.au-loader-flower {
  width: 20px;
  height: 20px;
  display: inline-block;
  vertical-align: middle;
  -webkit-animation: a 1s steps(12) infinite;
  animation: au-flower 1s steps(12) infinite;
  background: transparent
    url(data:image/svg+xml;base64,)
    no-repeat;
  background-size: 100%;
}

@keyframes au-flower {
  0% {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg);
  }

  to {
    -webkit-transform: rotate(1turn);
    transform: rotate(1turn);
  }
}

@-webkit-keyframes au-circle {
  0% {
    transform: rotate(0);
  }

  100% {
    transform: rotate(360deg);
  }
}
</style>
